document.addEventListener("DOMContentLoaded",function(){try{const e=document.getElementById("techCanvas");if(!e||!e.getContext)throw new Error("Canvas non supportato o non trovato");const t=e.getContext("2d");if(!t)throw new Error("Contesto 2D non disponibile");function n(){e.width=window.innerWidth,e.height=window.innerHeight}window.addEventListener("resize",n),n();const o=[],r=60;class i{constructor(){this.reset(),this.speed=3+5*Math.random()}reset(){this.x=Math.random()*e.width,this.y=-50,this.length=100+200*Math.random(),this.color=`rgba(0,255,136,${.3+.5*Math.random()})`,this.width=1+2*Math.random()}update(){this.y+=this.speed,this.y>e.height+this.length&&this.reset()}draw(){t.strokeStyle=this.color,t.lineWidth=this.width,t.beginPath(),t.moveTo(this.x,this.y),t.lineTo(this.x,this.y+this.length),t.stroke()}}for(let e=0;e<r;e++)setTimeout(()=>o.push(new i),100*e);function a(){t.clearRect(0,0,e.width,e.height),o.forEach(e=>{e.update(),e.draw()}),requestAnimationFrame(a)}a()}catch(e){console.error("Errore nell'animazione:",e)}});
